mapper.select:
SELECT MAX(id)
FROM learning_session
WHERE user_id = #{userId}

SELECT id_groupe id, nom_groupe name
FROM woonoz_groupe
WHERE id_groupe_p = #{areaId};

SELECT COUNT(*)
FROM woonoz_groupe_p_membre
WHERE id_groupe_p = #{areaId}
      AND user_role = 'learner';

SELECT COUNT(*)
FROM woonoz_groupe_membre
WHERE id_groupe = #{groupId};

SELECT id_groupe_p
FROM woonoz_groupe
WHERE id_groupe = #{groupId};
